package corejava;

public class For_loop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// looping means to continue
		// purpose of this progress is to print all the value i beginning with 0 out and 20
		// for looping it has increment only 1. cannot be twice
		
		for(int i=0;     // start value i=0
				i<20;    // condition
				i+=2)	
		{
			System.out.println(i);
			
		}
		
		
		
		
		
			}

}
