package corejava;

public class Single_array {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

	String Rainbow []=new String [7];	
		
	Rainbow [0] = "Red";
	Rainbow [1] = "Yellow";
	Rainbow [2] = "Blue";
	Rainbow [3] = "Green";	
	Rainbow [4] = "Violet";
	Rainbow [5] = "Indigo";
	Rainbow [6] = "Orange";
		
	//System.out.println (Rainbow [0]);
	//System.out.println (Rainbow [6]);
		
	for (int x=0; x<Rainbow.length;x++)     // gives you a table row count
	{
		 System.out.println(Rainbow[x]);
	}
		
		
	}

}
