package corejava;

public class While_looping {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
    int  x = 0; 				//starting value
    
      while ( x < 20 ) 	 		// condition
      {
    	System.out.println (x);
    	x+=1;
    	
    	
    	
      }
		
		
		
		
	}

}
