package corejava;

public class break_switch {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
	int age = 3;
	
	switch(age){
	
	case 1:
		System.out.println ("you can crawl");
		break;
		
	case 2:
		System.out.println("you can talk");
		break;
		
	case 3:
		System.out.println("you can walk");
		break;
		
	case 4:
		System.out.println("you can start eating");
		break;
		
	case 5:
		System.out.println("you can jumping around");
		break;
		
	case 6:
		System.out.println("you can start making a sound");
		break;	
		
	default:
		System.out.println("i dont know your age");
		break; 
		
		
		
		
		
		
		
		
		
		
		
		
		
		
	}	
	}

}
